sum_words.map:
function (doc) {
  if (doc.type == 'placename_holder') return;

  var words = doc.full_text.toLowerCase().replace(/[^a-z]+/g, " ").split(" ");
  for(word in words) {
    if(words[word]!="")
      emit(words[word], null);
  }
}

covid_occurance.map:
function (doc) {
  if (doc.type == 'placename_cache') return;

  var words = doc.text;
  if(doc.retweeted_status)
    words = doc.retweeted_status.full_text;
  words = doc.full_text || words;
  relevant = words.toLowerCase().includes("covid") || words.toLowerCase().includes("corona virus") || words.toLowerCase().includes("coronavirus");
  emit("relevant", relevant ? 1 : 0);
  emit("total", 1);
}

group_by_location.map:
function (doc) {
  if (doc.type == 'placename_holder') return;

  let val = {
    id: doc.id,
    full_text: doc.full_text,
    user_name: doc.user.screen_name
  };
  if (doc.geo != null){
    emit(['coordinate',[doc.geo[0], doc.geo[1]]], val);
  } else if (doc.coordinate != null){
    emit(['coordinate',[doc.coordinate[1], doc.coordinate[0]]], val);
  } else if (doc.place != null){
    emit(['place_name', doc.place.full_name], val);
  } else if (doc.user != null && doc.user.location != null && doc.user.location.trim().length > 0 ){
    emit(['place_name', doc.user.location.trim()], val);
  }
}
